    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        qs=2;
        Connect c = new Connect();
        c.deletePrev(qs);
        Connect.connection();
        qs=0;
        
        this.setVisible(false);
        new Screen_Q2().setVisible(true);

    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Connect c = new Connect();
           c.setQuestion(3);
           
           if(rb1.isSelected()){
            c.setAnswer("a) Selection operation");
           } 
           else if(rb2.isSelected()){
            c.setAnswer("b) Primitive operation");
           }
           else if(rb3.isSelected()){
            c.setAnswer("c) Projection operation");
           }
           else if(rb4.isSelected()){
            c.setAnswer("d) Generalized selection");
           }
           else {
            c.setAnswer("No Answer");
           }
        
        Connect.connection();
        c.setAnswer(null);
        
        this.setVisible(false);
        new Screen_Q4().setVisible(true);
    }//GEN-LAST:event_jButton2ActionPerformed

